-- 2. Update Cart
DELIMITER //
CREATE PROCEDURE UpdateCart()
BEGIN
    DECLARE current_id INT DEFAULT 350060;
    DECLARE end_id INT DEFAULT 350174;
    
    WHILE current_id <= end_id DO
        UPDATE CART_MFOODY
        SET
            QUANTITY_ALL_PRODUCTS_IN_CART = (
                SELECT SUM(QUANTITY_DETAIL_PRODUCT_CART)
                FROM DETAIL_PRODUCT_CART_MFOODY
                WHERE ID_CART = current_id
            ),
            TOTAL_SALE_PRICE_CART = (
                SELECT SUM(QUANTITY_DETAIL_PRODUCT_CART * SALE_PRICE_PRODUCT)
                FROM DETAIL_PRODUCT_CART_MFOODY, PRODUCT_MFOODY
                WHERE DETAIL_PRODUCT_CART_MFOODY.ID_PRODUCT = PRODUCT_MFOODY.ID_PRODUCT
                AND ID_CART = current_id
            ),
            TOTAL_FULL_PRICE_CART = (
                SELECT SUM(QUANTITY_DETAIL_PRODUCT_CART * FULL_PRICE_PRODUCT)
                FROM DETAIL_PRODUCT_CART_MFOODY, PRODUCT_MFOODY
                WHERE DETAIL_PRODUCT_CART_MFOODY.ID_PRODUCT = PRODUCT_MFOODY.ID_PRODUCT
                AND ID_CART = current_id
            )
        WHERE ID_CART = current_id;
        
        SET current_id = current_id + 1;
    END WHILE;
END //
DELIMITER ;
CALL UpdateCart();

