-- 4. Add Random Data to table DETAIL_PRODUCT_ORDER_MFOODY
DELIMITER //
CREATE PROCEDURE InsertDetailProductOrderMfoody()
BEGIN
    DECLARE startOrderId INT DEFAULT 750020;
    DECLARE endOrderId INT DEFAULT 750402;
    DECLARE startProductId INT DEFAULT 530020;
    DECLARE endProductId INT DEFAULT 530262;
    DECLARE currentOrderId INT;
    DECLARE currentProductId INT;
    DECLARE maxProductsPerOrder INT DEFAULT 5;
    DECLARE numProductsToAdd INT;
    DECLARE existingProductIds VARCHAR(255);
    DECLARE newProductId INT;

    SET currentOrderId = startOrderId;

    WHILE currentOrderId <= endOrderId DO
        SET numProductsToAdd = FLOOR(RAND() * (maxProductsPerOrder - 1)) + 1;
        SET existingProductIds = '';

        WHILE numProductsToAdd > 0 DO
            SET newProductId = FLOOR(RAND() * (endProductId - startProductId + 1)) + startProductId;

            IF FIND_IN_SET(newProductId, existingProductIds) = 0 THEN
                INSERT INTO DETAIL_PRODUCT_ORDER_MFOODY (
                    ID_ORDER,
                    ID_PRODUCT,
                    QUANTITY_DETAIL_PRODUCT_ORDER,
                    SALE_PRICE_DETAIL_PRODUCT_ORDER,
                    FULL_PRICE_DETAIL_PRODUCT_ORDER
                )
                VALUES (
                    currentOrderId,
                    newProductId,
                    FLOOR(RAND() * 5) + 1,
                    (SELECT SALE_PRICE_PRODUCT FROM PRODUCT_MFOODY WHERE ID_PRODUCT = newProductId),
                    (SELECT FULL_PRICE_PRODUCT FROM PRODUCT_MFOODY WHERE ID_PRODUCT = newProductId)
                );

                SET existingProductIds = CONCAT(existingProductIds, ',', newProductId);
                SET numProductsToAdd = numProductsToAdd - 1;
            END IF;
        END WHILE;

        SET currentOrderId = currentOrderId + 1;
    END WHILE;
END //
DELIMITER ;
CALL InsertDetailProductOrderMfoody();

