                FROM DETAIL_PRODUCT_ORDER_MFOODY
                WHERE ID_ORDER = current_id
            ),
            TOTAL_SALE_PRICE_ORDER = (
                SELECT SUM(QUANTITY_DETAIL_PRODUCT_ORDER * SALE_PRICE_PRODUCT)
                FROM DETAIL_PRODUCT_ORDER_MFOODY, PRODUCT_MFOODY
                WHERE DETAIL_PRODUCT_ORDER_MFOODY.ID_PRODUCT = PRODUCT_MFOODY.ID_PRODUCT
                AND ID_ORDER = current_id
            ),
            TOTAL_FULL_PRICE_ORDER = (
                SELECT SUM(QUANTITY_DETAIL_PRODUCT_ORDER * FULL_PRICE_PRODUCT)
                FROM DETAIL_PRODUCT_ORDER_MFOODY, PRODUCT_MFOODY
                WHERE DETAIL_PRODUCT_ORDER_MFOODY.ID_PRODUCT = PRODUCT_MFOODY.ID_PRODUCT
                AND ID_ORDER = current_id
            )
        WHERE ID_ORDER = current_id;
        
        SET current_id = current_id + 1;
    END WHILE;
END //
DELIMITER ;
CALL UpdateOrder();

-- 6. Update Ratting Product
DELIMITER //
CREATE PROCEDURE UpdateProduct()
BEGIN
    DECLARE current_id INT DEFAULT 530020;
    DECLARE end_id INT DEFAULT 530262;
    DECLARE avg_rating DECIMAL(10, 2);
    
    WHILE current_id <= end_id DO
        SET avg_rating = (
            SELECT COALESCE(AVG(RATING_COMMENT), 0)
            FROM COMMENT_MFOODY
            WHERE ID_PRODUCT = current_id
        );
        
        UPDATE PRODUCT_MFOODY
        SET RATING_PRODUCT = avg_rating
        WHERE ID_PRODUCT = current_id;
        
        SET current_id = current_id + 1;
    END WHILE;
END //
DELIMITER ;
CALL UpdateProduct();

